// Generated by CoffeeScript 1.3.3
(function() {

  $(document).ready(function() {
    /*
      Displays the scores of all the players currently in the trivia room
    */
    return window.ScoreboardView = Backbone.View.extend({
      el: $('#num-of-players'),
      initialize: function() {
        this.model.bind("updatePlayerCountEvent", this.updatePlayerCount, this);
        return this.model.bind("updateScoreboardEvent", this.updateScoreboard, this);
      },
      updatePlayerCount: function(count) {
        return this.$el.find($('h2.players')).html(count);
      },
      updateScoreboard: function(players) {
        var i, length, player, _results;
        $('#player-list').find('li').remove();
        i = 0;
        length = players.length;
        _results = [];
        while (i < length) {
          player = players[i];
          /*
                  '<li>'+
                    '<div class="row">' +
                      '<div class="player span2">Player ' + player.id + '</div>' +
                      '<div class="score span1 offset1">' + player.score + '</div>' +
                    '</div>' +
                  '</li>'
          */

          $('#player-list').append("<li>" + "<div class=\"row\">" + "<div class=\"player span2\">Player " + player.id + "</div>" + "<div class=\"score span1 offset1\">" + player.score + "</div>" + "</div>" + "</li>");
          _results.push(i++);
        }
        return _results;
      }
    });
  });

}).call(this);
